import { Tab } from "@ya.praktikum/react-developer-burger-ui-components";
import { useState, useRef } from "react";
import PropTypes from "prop-types";
import IngredientItem from "../Ingredient/Ingredient";
import style from "./BurgerIngredients.module.css";
import Modal from "../Modal/Modal";
import IngredientDetails from "../IngredientDetails/IngredientDetails";

function BurgerIngredients({ data }) {
  const [isOpen, setIsOpen] = useState(false);
  const [myIngredient, setCurrentIngredient] = useState(null);

  const handleOpenModal = (item) => {
    setIsOpen(true);
    setCurrentIngredient(item);
  };

  const handleCloseModal = () => {
    setIsOpen(false);
    setCurrentIngredient(null);
  };

  const [current, setCurrent] = useState("buns");

  const tabsRefs = {
    bun: useRef(null),
    sauce: useRef(null),
    main: useRef(null),
  };

  const tabsClick = (item) => {
    setCurrent(item);
    if (tabsRefs[item].current) {
      tabsRefs[item].current.scrollIntoView({ behavior: "auto" });
    }
  };

  const bun = data.filter((ingredient) => ingredient.type === "bun");
  const sauce = data.filter((ingredient) => ingredient.type === "sauce");
  const main = data.filter((ingredient) => ingredient.type === "main");

  return (
    <section className={style.burgerIngredients}>
      <h1 className="text text_type_main-large">Соберите бургер</h1>
      <div className={style.tab}>
        <Tab value="bun" active={current === "bun"} onClick={() => tabsClick("bun")}>
          Булки
        </Tab>
        <Tab value="sauce" active={current === "sauce"} onClick={() => tabsClick("sauce")}>
          Соусы
        </Tab>
        <Tab value="main" active={current === "main"} onClick={() => tabsClick("main")}>
          Начинки
        </Tab>
      </div>
      <div className={`custom-scroll ${style.scroll_ingredients}`}>
        {/* Mapping bun, sauce, and main lists here */}
      </div>
      {isOpen && (
        <Modal closePopup={handleCloseModal}>
          <IngredientDetails myIngredient={myIngredient} />
        </Modal>
      )}
    </section>
  );
}

BurgerIngredients.propTypes = {
  data: PropTypes.arrayOf(PropTypes.shape({
    // Define your ingredientPropType shape here
  })).isRequired,
};

export default BurgerIngredients;
